events.inlineCode:
runtimeScene.setBackgroundColor(100,100,240);
function gvar(variable){
    return runtimeScene.getVariables().get(variable).getAsNumber()
}
let scores = [gvar("a1count"),gvar("a2count"),gvar("a3count"),gvar("a4count")]
console.log(scores);

let first = scores.indexOf(Math.max.apply(Math,scores))+1;
scores[first-1] = -1
let globalvar = runtimeScene.getGame().getVariables().get("p"+first+"_global")
globalvar.setNumber(globalvar.getAsNumber()+3)
console.log("First ="+(first));

let second = scores.indexOf(Math.max.apply(Math,scores))+1;
scores[second-1] = -1
globalvar = runtimeScene.getGame().getVariables().get("p"+second+"_global")
globalvar.setNumber(globalvar.getAsNumber()+2)
console.log("Second ="+(second));

let third = scores.indexOf(Math.max.apply(Math,scores))+1;
scores[third-1] = -1
globalvar = runtimeScene.getGame().getVariables().get("p"+third+"_global")
globalvar.setNumber(globalvar.getAsNumber()+1)
console.log("Third ="+(third));
